package com.schedule.dao.domain;import org.apache.commons.lang3.builder.ToStringBuilder;import org.apache.commons.lang3.builder.ToStringStyle;/** * Created by fengwei.cfw on 2017/4/26. */public class CommonResult {    private boolean success;    private String code;    private String message;    private Object data;    public CommonResult() {    }    public CommonResult(boolean success, String message, Object data, String code) {        this.success = success;        this.code = code;        this.message = message;        this.data = data;    }    public static CommonResult success() {        return new CommonResult(true, null, null, null);    }    public static CommonResult success(Object object) {        return new CommonResult(true, null, object, null);    }    public static CommonResult success(String message, Object object) {        return new CommonResult(true, message, object, null);    }    public static CommonResult fail(String message) {        return new CommonResult(false, message, null, null);    }    public static CommonResult fail(String code, String message) {        return new CommonResult(false, message, null, code);    }    public boolean isSuccess() {        return success;    }    public void setSuccess(boolean success) {        this.success = success;    }    public String getCode() {        return code;    }    public void setCode(String code) {        this.code = code;    }    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }    public Object getObject() {        return data;    }    public void setObject(Object object) {        this.data = object;    }    @Override    public String toString() {        return ToStringBuilder.reflectionToString(this, ToStringStyle.SHORT_PREFIX_STYLE);    }}