package com.schedule.dao.domain;import org.apache.commons.lang3.builder.ToStringBuilder;import org.apache.commons.lang3.builder.ToStringStyle;/** * Created by fengwei.cfw on 2017/2/23. */public class PageQuery {    private int currentPage = 1;    private int pageSize = 10;    private int limit;    private int offset;    public int getCurrentPage() {        if (currentPage == 0){            return 1;        }        return currentPage;    }    public void setCurrentPage(int currentPage) {        this.currentPage = currentPage;    }    public int getPageSize() {        if (pageSize == 0){            return 10;        }        return pageSize;    }    public void setPageSize(int pageSize) {        this.pageSize = pageSize;    }    public int getLimit() {        return limit;    }    public void setLimit(int limit) {        this.limit = limit;    }    public int getOffset() {        return offset;    }    public void setOffset(int offset) {        this.offset = offset;    }    @Override    public String toString() {        return ToStringBuilder.reflectionToString(this, ToStringStyle.SHORT_PREFIX_STYLE);    }}