package com.schedule.dao.domain;import org.apache.commons.lang3.builder.ToStringBuilder;import org.apache.commons.lang3.builder.ToStringStyle;import java.io.Serializable;import java.util.ArrayList;import java.util.List;/** * Created by fengwei.cfw on 2017/7/14. */public class PaginationResult<T> implements Serializable {    private int totalPage;    private int currentPage;    private int num;    private List<T> list = new ArrayList<T>();    public PaginationResult(int totalPage, int currentPage, int number, List<T> objects) {        this.totalPage = totalPage;        this.currentPage = currentPage;        this.num = number;        this.list = objects;    }    public int getTotalPage() {        return totalPage;    }    public void setTotalPage(int totalPage) {        this.totalPage = totalPage;    }    public int getCurrentPage() {        return currentPage;    }    public void setCurrentPage(int currentPage) {        this.currentPage = currentPage;    }    public int getNum() {        return num;    }    public void setNum(int num) {        this.num = num;    }    public List<T> getList() {        return list;    }    public void setList(List<T> list) {        this.list = list;    }    public static class PaginationUtils {        public static int getPages(int a, int b) {            return ((double) a / (double) b) > (a / b) ? a / b + 1 : a / b;        }    }    @Override    public String toString() {        return ToStringBuilder.reflectionToString(this, ToStringStyle.SHORT_PREFIX_STYLE);    }}