package com.schedule.util;import org.apache.commons.lang3.StringUtils;/** * Created by demopoo on 2017/2/22. */public class Precondition {    public static <T> T checkNotNull(T reference) {        if (reference == null) {            throw new PreconditonException();        }        if (reference instanceof String) {            if (StringUtils.isBlank(reference.toString())) {                throw new PreconditonException();            }        }        return reference;    }    public static <T> T checkNotNull(T reference, String message) {        if (reference == null) {            throw new PreconditonException(message);        }        if (reference instanceof String) {            if (StringUtils.isBlank(reference.toString())) {                throw new PreconditonException(message);            }        }        return reference;    }    public static void checkArgument(boolean expression) {        if (!expression) {            throw new PreconditonException();        }    }    public static void checkArgument(boolean expression, Object errorMessage) {        if (!expression) {            throw new PreconditonException(String.valueOf(errorMessage));        }    }}